styles.raw:
@import url('https://fonts.googleapis.com/css2?family=Lato:ital,wght@0,400;0,700;1,400&family=Libre+Baskerville:ital@0;1&display=swap');

body {
  --primary-verylight: #8B7AAE;
  --primary-light: #655091;
  --primary: #452F74;
  --primary-dark: #2B1657;
  --primary-verydark: #16063A;
  --secondary1-verylight: #9C73A9;
  --secondary1-light: #7D498D;
  --secondary1: #5F2871;
  --secondary1-dark: #441154;
  --secondary1-verydark: #2B0238;
  --secondary2-verylight: #7B85AE;
  --secondary2-light: #515E91;
  --secondary2: #303D74;
  --secondary2-dark: #172457;
  --secondary2-verydark: #07113A;
  background-color: Aliceblue;
}
img {
	display: block;
	max-width: 100%;
}
.visually-hidden {
  border: 0;
  clip: rect(0 0 0 0);
  height: 1px;
  margin: -1px;
  overflow: hidden;
  padding: 0;
  position: absolute;
  width: 1px;
}

.primo-copy {
  margin: 0 auto;
  width: 100%;
  padding-right: 2rem;
  padding-left: 2rem;
  font-family: 'Lato', sans-serif;
  font-size: 1.125rem;
  color: var(--primary-verydark);

  h1 { 
    font-size: 1.875rem;
    font-weight: 500;
  }

  h2 {
    font-size: 1.5rem;
    font-weight: 500;
    padding: 2rem 0 1rem 0;
  }

  h1, h2 {
    font-family: 'Libre Baskerville', serif;
    color: var(--primary-dark);
    filter: drop-shadow(0 1px 1px rgba(0, 0, 0, 0.1));
  }

  ol {
    list-style-type: decimal;
    list-style-position: inside;
  }

  ul {
    list-style-type: disc;
    list-style-position: outside;
  }

  li {
    padding: .2rem;
  }

  p {
    display: block;
    padding: .5rem 0;
  }

  a {
    color: var(--secondary2);
    text-decoration: underline;
  }
	a:visited {
    color: var(--primary);
  }
  a:hover {
    color: var(--secondary1-light);
  }
  blockquote {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1),
      0 2px 4px -1px rgba(0, 0, 0, 0.06);
    padding: 1.5rem;
  }

  mark {
    --text-opacity: 1;
    color: #161e2e;
    --bg-opacity: 1;
    background-color: #fce96a;
  }
}
@media (min-width: 640px) {
  .primo-copy {
    width: 80%;
    max-width: 1400px;
  }
}

@media (min-width: 1024px) {
  .primo-copy h1 {
    font-size: 3rem;
  }

  .primo-copy h2 {
    font-size: 2.25rem;
  }
}

